import React, { Component } from 'react';
import './App.css';
import Container from 'react-bootstrap/Container';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import ListTodos from './ListTodos/ListTodos.js';
import {generate} from 'randomstring';

class App extends Component {
  state = {
    "Contracts": [
      { "key": generate(10),
      "FirstName": "Cathy" ,
      "LastName": "Pierce",
      "Birthday": "9/14/1996",
      "Telephone": "200-910-8132"},

      { "key": generate(10),
      "FirstName": "Alfonso",
      "LastName": "Cooley",
      "Birthday": "8/10/1973",
      "Telephone": "200-657-9362"},

      { "key": generate(10),
      "FirstName": "Victor",
      "LastName": "Gordon",
"Birthday":  "8/3/1970",
"Telephone": "200-661-9407"},

      { "key": generate(10),
      "FirstName": "Cathy" ,
      "LastName": "Pierce",
      "Birthday": "9/14/1996",
      "Telephone": "200-910-8132"},
    ],
    "collapse": false,
    "formTitle": '',
    "formDescription": ''
  }

  addTodoHandler = (event) => {
    event.preventDefault();
    let newContracts = {
      key: generate(10),
      FirstName: this.state.newFirstName,
      LastName: this.state.newLastName,
      Birthday: this.state.newBirthday,
      Telephone: this.state.newTelephone
    };

    this.setState({todos:[...this.state.todos,newTodo]});
    this.setState({formFirstName:''});
    this.setState({formLastName:''});
    this.setState({formBirthday:''});
    this.setState({formTelephone:''})
  }

  closeTodoHandler = (key,e) => {
    let todos = [...this.state.todos];
    let deleteIndex = todos.findIndex((item)=>item.key===key);
    todos.splice(deleteIndex, 1);
    this.setState({"todos":todos});
  }

  render = () => {
    return (
      <div className="App">
        <Container>
          <header className="App-header text-left">
            <h1>React Based ToDo List</h1>
          </header>
          <ListTodos 
            todos={this.state.todos}
            closer={this.closeTodoHandler}>
          </ListTodos>
          <h2 className='text-left'>Add A ToDo</h2>
          <Form className="text-left" onSubmit={this.addTodoHandler}>
            <Form.Group controlId="formToDo">
              <Form.Label>Title</Form.Label>
              <Form.Control 
                type="text" 
                placeholder="Enter Title" 
                value={this.state.formTitle}
                onChange={(e) => this.setState({formTitle: e.target.value})}/>
              <Form.Label>Description</Form.Label>
              <Form.Control as="textarea" 
                rows="3" 
                placeholder="Enter Description" 
                value={this.state.formDescription}
                onChange={(e) => this.setState({formDescription:e.target.value})}/>
            </Form.Group>
            <Button variant="primary" type="submit" >
            Add Todo
            </Button>
          </Form>
        </Container>
      </div>
    );
  }
}

export default App;
